<div ng-controller="authenticationController" class="login-container"><h2 class="center-justify">login</h2><div class="row center-justify form-group"><br/><input type="text" placeholder="username" ng-model="u.name" autofocus="autofocus" class="form-control tenth center"/><input type="password" placeholder="password" ng-model="u.pass" class="form-control tenth center"/><br/><div role="alert" ng-if="message.text" class="message alert {{message.type}}">{{message.text}}</div><br/><button type="submit" ng-click="login()" class="btn btn-primary">let's go</button></div></div>